    $('[data-slider="tabslider"]').on('beforeChange', function (event, slick, currentSlide, nextSlide) {
      $('[data-tab]').removeClass('active');
      $(`[data-tab=${nextSlide + 1}]`).addClass('active');
    });
    /*   $('[data-slider="tabslider"]').on('afterChange', function(event, slick, currentSlide, nextSlide){
        $('[data-slider="tabslider"] .slick-list').css('height', $('[data-slider="tabslider"] .slick-current').height())
      }); */
    
    $('[data-tab]').on('click', function () {
      $('[data-tab]').removeClass('active');
      $(this).addClass('active');
      let index = parseInt($(this).attr('data-tab'));
      $('[data-slider="tabslider"]').slick('slickGoTo', index - 1);
    
    
    })
    var options = {
      infinite: false,
      nextArrow: '.tabslider__arrow_right',
      prevArrow: '.tabslider__arrow_left',
      responsive: [
        {
          breakpoint: 999,
          settings: {
            vertical: true,
            infinite: false,
    
            nextArrow: '.tabslider__arrow_right_mb',
            prevArrow: '.tabslider__arrow_left_mb',
          }
        },
      ]
    }
    
    
    const mySlider = $('[data-slider="tabslider"]').on('init', function (slick) {
    
      // on init run our multi slide adaptive height function
      multiSlideAdaptiveHeight(this);
    
    }).on('afterChange', function (slick, currentSlide, nextSlide) {
    
      // on afterChange run our multi slide adaptive height function
      multiSlideAdaptiveHeight(this);
    
    }).on('beforeChange', function (slick, currentSlide, nextSlide) {
    
      // on beforeChange run our multi slide adaptive height function
      multiSlideAdaptiveHeight(this);
    
    }).slick(options);
    
    function multiSlideAdaptiveHeight(slider) {
    
      // set our vars
      let activeSlides = [];
      let tallestSlide = 0;
    
      // very short delay in order for us get the correct active slides
      setTimeout(function () {
    
        // loop through each active slide for our current slider
        $('.slick-track .slick-active', slider).each(function (item) {
    
          // add current active slide height to our active slides array
          activeSlides[item] = $(this).outerHeight();
    
        });
    
        // for each of the active slides heights
        activeSlides.forEach(function (item) {
    
          // if current active slide height is greater than tallest slide height
          if (item > tallestSlide) {
    
            // override tallest slide height to current active slide height
            tallestSlide = item;
    
          }
    
        });
    
        // set the current slider slick list height to current active tallest slide height
        $('.slick-list', slider).height(tallestSlide);
    
      }, 10);
    
    }
    
    
    // when window is resized
    $(window).on('resize', function () {
    
      // run our multi slide adaptive height function incase current slider active slides change height responsively
      multiSlideAdaptiveHeight(mySlider);
    
    });
  </script>
